function beautify(x, n) {
	if (x >= 1e9) {
		var z = Math.floor(logFloor(x)/3);
		var prefixes = ["billion ","trillion ","quadrillion ","quintillion ","sextillion ","septillion ", "octillion ", "nonillion "];
		var s = beautify(x/Math.pow(10,3*z),n);
		return s+" "+prefixes[z-3];
	} else {
		return numberWithCommas(x.toFixed(n));
	};
};
function numberWithCommas(n) {
    var parts=n.toString().split(".");
    return parts[0].replace(/\B(?=(\d{3})+(?!\d))/g, ",") + (parts[1] ? "." + parts[1] : "");
};
function logFloor(x) {
	var count = 0;
	while (x >= 10) {
		count++;
		x /= 10;
	};
	return count;
};
function fix(x) {
	if (x >= 1e9) {
		return beautify(x, 3);
	} else {
		return beautify(x, 2);
	};
};